import CartContext from "./cart-context";
import { useReducer } from "react";

// const defaultCart = {
//   items: [],
//   jumlah: 0,
// };

const prosesCart = (state, action) => {
  if (action.type === "ADD") {
    const updatedItems = state.items.concat(action.item);
    const totalJumlah = state.jumlah + action.item.price * action.item.harga; //!THEFUCK?!!
    console.log(`test`, action.item.harga, action.item.price);
    return {
      items: updatedItems,
      jumlah: totalJumlah,
    };
  }

  return defaultCart;
};

const CartProvider = (props) => {
  const [cartState, dispatchCartState] = useReducer(
    prosesCart,
    defaultCart({
      items: [],
      jumlah: 0,
    })
  );

  const tambahItem = (i) => {
    dispatchCartState({
      type: "ADD",
      item: i,
    });
  };
  const hapusItem = (id) => {
    dispatchCartState({
      type: "REMOVE",
      id: id,
    });
  };

  const cartContext = {
    items: cartState.items,
    jumlah: cartState.jumlah,
    addBarang: tambahItem,
    removeBarang: hapusItem,
  };
  return (
    <CartContext.Provider value={cartContext}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartProvider;
