import { Fragment } from "react";
import { ReactDOM } from "react-dom";
import modalStyle from "./Modal.module.css";

const Backdrop = (props) => {
  return <div className={modalStyle.backdrop}></div>;
};
const OverlayModal = (props) => {
  return (
    <div className={modalStyle.modal}>
      <div className={modalStyle.content}>{props.children}</div>
    </div>
  );
};

const tempatPortal = document.getElementById(`Modal`);

const Modal = (props) => {
  return (
    <Fragment>
      {ReactDOM.createProtal(<Backdrop />, tempatPortal)}
      {ReactDOM.createProtal(
        <OverlayModal>{props.children}</OverlayModal>,
        tempatPortal
      )}
    </Fragment>
  );
};

export default Modal;
