import CartContext from "./cart-context";
import { useReducer } from "react";

const defaultCart = {
  items: [],
  jumlah: "0",
};

const prosesCart = (state, action) => {
  if (action.type === "ADD") {
    const updatedItems = state.items.concat(action.item);
    const totalJumlah = state.jumlah + action.item.jumlah * action.item.harga; //!THEFUCK?!!
    const updatedTotal = totalJumlah + action.item.jumlah * action.item.harga;
    console.log(
      `jlh`,
      action.item.jumlah,
      `harga`,
      action.item.harga,
      "state.jlh",
      state.jumlah,
      `total.jlh`,
      totalJumlah,
      `updateTot`,
      updatedTotal
    );
    // console.log(`test`, updatedItems);
    return {
      items: updatedItems,
      jumlah: totalJumlah,
    };
  }

  return defaultCart;
};

const CartProvider = (props) => {
  const [cartState, dispatchCartState] = useReducer(prosesCart, defaultCart);

  const tambahItem = (i) => {
    dispatchCartState({
      type: "ADD",
      item: i,
    });
    // console.log(`i`, i);
  };
  const hapusItem = (id) => {
    dispatchCartState({
      type: "REMOVE",
      id: id,
    });
  };

  const cartContext = {
    items: cartState.items,
    jumlah: cartState.jumlah,
    addBarang: tambahItem,
    removeBarang: hapusItem,
  };
  return (
    <CartContext.Provider value={cartContext}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartProvider;
