import cartStyle from "./Cart.module.css";
import Modal from "../UI/Modal";
// import Card from "../UI/Card";
import { useContext } from "react";
import CartContext from "../../store/cart-context";
import CartItem from "./CartItem";

const Cart = (props) => {
  const cartCtx = useContext(CartContext);
  const hargaBarang = `Rp${cartCtx.jumlah}`;
  const adaItemGa = cartCtx.items.length > 0;

  const cartItemRemoveHdlr = (id) => {
    cartCtx.map((e) => e.id != e.id);
  };

  const cartItemAddhdlr = (item) => {};

  const cartItems = (
    <ul className={cartStyle[`cart-items`]}>
      {cartCtx.items.map((e) => (
        <CartItem
          key={e.id}
          name={e.name}
          amount={e.jumlah}
          price={e.harga}
          onRemove={cartItemRemoveHdlr.bind(null, item.id)}
          onAdd={cartItemAddhdlr.bind(null, item)}
        />
      ))}{" "}
    </ul>
  );

  return (
    <Modal onClose={props.onTutupCart}>
      {cartItems}
      <div className={cartStyle.total}>
        <span>Total Amount: </span>
        <span>{hargaBarang}</span>
      </div>
      <div className={cartStyle.actions}>
        <button
          className={cartStyle[`button--alt`]}
          onClick={props.onTutupCart}
        >
          Close
        </button>
        {adaItemGa && <button className={cartStyle.button}>Order</button>}
      </div>
    </Modal>
  );
};

export default Cart;
